        <h1>ユーザー登録ページ</h1>
        <TextField
         value={password}
         fullWidth 
         label="e-mail" 
         variant="outlined"
         />
        <TextField fullWidth label="パスワード" variant="filled"/>
        <TextField fullWidth label="ユーザーネーム" variant="standard"/>
        <Button fullWidth variant="contained" color="primary">
            登録
        </Button>
        <Link to="./Login">アカウントを既にお持ちの方</Link>
        </form>
    )
};

export default Signup;
